import React, { useState, useEffect } from "react";
import "./button.scss";

const Button = (props) => {
  let timeout;
  const [active, setActive] = useState(false);

  const hover = () => {
    timeout = setTimeout(() => {
      setActive(true);
    }, props.delay || 400);
  };

  const mouseOut = () => {
    clearInterval(timeout);
    setActive(false);
  };
  const colorType = ["primary", "success", "warning", "danger", "infor"];
  const colorTypeToHex = [
    "#409eff",
    "#67c23a",
    "#e6a23c",
    "#f56c6c",
    "#909399",
  ];
  const renderColorType = () => {
    let findColorTypeIndex = colorType.indexOf(props.type);
    let btnType;
    if (props.type && findColorTypeIndex > -1) {
      btnType = colorTypeToHex[findColorTypeIndex];
      return btnType;
    }
  };
  useEffect(() => {
    renderColorType();
  });
  return (
    <button style={{ background: {} }} className={`button ${props.type}`}>
      {props.children}
    </button>
  );
};

export default Button;

