import React, { useState, useEffect } from "react";
import "./button.scss";

const Button = (props) => {
  const colorType = ["primary", "success", "warning", "danger", "infor"];
  const colorTypeToHex = [
    "#409eff",
    "#67c23a",
    "#e6a23c",
    "#f56c6c",
    "#909399",
  ];
  const renderColorType = () => {
    let findColorTypeIndex = colorType.indexOf(props.type);
    let btnType;
    if (props.type && findColorTypeIndex > -1) {
      btnType = colorTypeToHex[findColorTypeIndex];
      return btnType;
    }
  };

  return (
    <button
      style={{ background: `${renderColorType()}` }}
      className={`button ${props.shape || ""}`}
    >
      {props.children}
    </button>
  );
};

export default Button;

