import React, { useState, useEffect } from "react";
import "./button.scss";

const Button = (props) => {
  const colorType = ["primary", "success", "warning", "danger", "info"];
  const colorTypeToHex = [
    "#409eff",
    "#67c23a",
    "#e6a23c",
    "#f56c6c",
    "#909399",
  ];
  const renderColorType = () => {
    let findColorTypeIndex = colorType.indexOf(props.type);
    let btnType;
    if (props.type && findColorTypeIndex > -1) {
      btnType = colorTypeToHex[findColorTypeIndex];
    } else if (props.type && findColorTypeIndex === -1) {
      btnType = props.type;
    } else {
      btnType = "#fff";
    }
    return btnType;
  };
  const renderTextColor = () => {
    let textColor;
    if (props.type) {
      textColor = "#fff";
    } else {
      textColor = "#606266";
    }
    return textColor;
  };
  const onClick = (e) => {
    if (props.disable && props.disable === true) {
      e.preventDefault();
      return;
    }
    if (props.onClick) {
      return props.onClick(e);
    }
  };
  const handleClick = () => {
    onClick();
  };
  useEffect(() => {
    console.log(props);
  }, props.style);

  return (
    <button
      style={{
        background: `${renderColorType()}`,
        color: `${renderTextColor()}`,
        ...props.style,
      }}
      className={`button ${props.shape || ""}`}
      onClick={onClick}
    >
      {props.children}
    </button>
  );
};

export default Button;

