import React, { useState } from "react";
import "./button.scss";

const Button = (props) => {
  let timeout;
  const [active, setActive] = useState(false);

  const hover = () => {
    timeout = setTimeout(() => {
      setActive(true);
    }, props.delay || 400);
  };

  const mouseOut = () => {
    clearInterval(timeout);
    setActive(false);
  };
  const colorType = ["primary", "success", "warning", "danger", "infor"];
  const colorTypeToHex = [
    "#409eff",
    "#67c23a",
    "#e6a23c",
    "#f56c6c",
    "#909399",
  ];
  return <button className={`button ${props.type}`}>{props.children}</button>;
};

export default Button;

